$(function () { $(".change-devour").on("click", function () { var a = $(this).data("id"), b = $(this).data("devour"); $.ajax("/api/burgers/" + a, { type: "PUT", data: { devoured: b } }).then(function () { console.log("changed devour to", b), location.reload() }) }), $(".create-form").on("submit", function (a) { a.preventDefault(); var b = { burger_name: $("#brgr").val().trim() }; $.ajax("/api/burgers", { type: "POST", data: b }).then(function () { console.log("created the burger"), location.reload() }) }), $(".delete-burger").on("click", function () { var a = $(this).data("id"); $.ajax("/api/burgers/" + a, { type: "DELETE" }).then(function () { console.log("deleted the burger", a), location.reload() }) }) });